import React, { useState } from 'react';
import axios from 'axios';

function Upload() {
  const [file, setFile] = useState(null);
  const [insights, setInsights] = useState('');
  const [error, setError] = useState('');

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('industry', 'Manufacturing');  // Or make this a dropdown

    try {
      const response = await axios.post('http://127.0.0.1:8000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setInsights(response.data.insights);  // Display AI insights
      setError('');  // Clear any previous errors
    } catch (err) {
      console.error('Upload error:', err);  // Check console for details
      setError('Upload failed. Check console for details.');
      setInsights('');
    }
  };

  return (
    <div>
      <h2>Upload Financial Data</h2>
      <input type="file" accept=".csv,.xlsx,.pdf" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={handleUpload}>Upload</button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {insights && <p><strong>Insights:</strong> {insights}</p>}
    </div>
  );
}

export default Upload;
